(()=>{"use strict";!function(e){const n=document.getElementById("current-location"),t=document.getElementById("current-temperature"),o=document.getElementById("current-high"),r=document.getElementById("current-low");fetch("http://api.openweathermap.org/data/2.5/weather?q=New York&APPID=e6ea60ae42c4f7ea5dbffec273b1f3a0&units=imperial",{mode:"cors"}).then((e=>e.json())).then((e=>{n.innerHTML=e.name,t.innerHTML=`${Math.round(e.main.temp)}°`,o.innerHTML=`${Math.round(e.main.temp_max)}°`,r.innerHTML=`${Math.round(e.main.temp_min)}°`,console.log(e)})).catch((e=>{console.log(e)}))}()})();