(()=>{"use strict";!function(e){const t=document.getElementById("current-location"),n=document.getElementById("current-temperature"),a=document.getElementById("current-high"),o=document.getElementById("current-low"),r=document.getElementById("current-weather-icon");fetch("http://api.openweathermap.org/data/2.5/weather?q=New York&APPID=e6ea60ae42c4f7ea5dbffec273b1f3a0&units=imperial",{mode:"cors"}).then((e=>e.json())).then((e=>{t.innerHTML=e.name,n.innerHTML=`${Math.round(e.main.temp)}°`,a.innerHTML=`${Math.round(e.main.temp_max)}°`,o.innerHTML=` ${Math.round(e.main.temp_min)}°`,r.src=`http://openweathermap.org/img/wn/${e.weather[0].icon}@2x.png`,console.log(e)})).catch((e=>{console.log(e)}))}(),function(e){const t=document.getElementById("summary");fetch("http://api.openweathermap.org/data/2.5/weather?q=New York&APPID=e6ea60ae42c4f7ea5dbffec273b1f3a0&units=imperial",{mode:"cors"}).then((e=>e.json())).then((e=>{var n;t.innerHTML=`${n=e.weather[0].description,"string"!=typeof n?"":n.charAt(0).toUpperCase()+n.slice(1)}. The high is ${Math.round(e.main.temp_max)}° and the low is ${Math.round(e.main.temp_min)}°.`})).catch((e=>{console.log(e)}))}()})();